import aiml
import os
from tkinter import *
import tkinter.messagebox
import espeakng
import tkinter as tk

root = Tk()
root.title("AIAY")

bot = aiml.Kernel()
bot.learn("file.aiml")

def suara(event):
    a = bot.respond(masukan.get())
    # tkinter.messagebox
    print(a)
    os.system('espeak.exe %(a)s' % locals())
    masukan.delete(0, END)

atas = Frame(root).pack()
bawah = Frame(root).pack(side=BOTTOM)

foto = PhotoImage(file="chatbot1.png")
fotone = Label(atas, image=foto).pack()

masukan = Entry(bawah, width=70)
masukan.pack()

masukan.bind("<Shift-Up>", suara)

b = '"WELCOME TO CHATBOT"'
os.system('espeak.exe %(b)s' % locals())

root.mainloop()